sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var si = new Slacker(),
		payload = request.body.data;

	if( payload.challenge ){
		si.debug( 'A URL Validation Challenge has been issued. Sending the Challenge response.' );

		response.setStatus( 200 );
		response.setContentType( 'text/plain' );
		response.getStreamWriter().writeString( payload.challenge );
	}
	else{
		si.debug( 'Saving the event payload.' );

		new global.GlideQuery( si.table_payload )
		.insert( {
			'payload': JSON.stringify( payload, null, 2 ),
			'channel': payload.event.channel,
			'timestamp': payload.event.ts,
			'thread': payload.event.thread_ts || ''
		} );
	}

})(request, response);
